class Solution {
public:
    vector<int> sortArrayByParityII(vector<int>& a) {

        int n  = a.size();
        int l = 0;
        int r = n-1;
        int sa, sb;
        sa = sb = -1;
        while(l<n && r>=0)
        {
            while(l<n && r>=0 && (l%2 ==0 && a[l]%2 == 0) || l%2)
            {
                l++;  
            }

            if(l<n && r>=0 && (l%2 ==0 && a[l]%2 != 0))
            {
                sa = l;
            }


            while(l<n && r>=0 && (r%2 && a[r]%2) || r%2==0)
            {
                r--;
            }
            if(l<n && r>=0 && (r%2 && a[r]%2 == 0))
            {
                sb = r;
            }

            if(sa >=0 && sa < n && sb>=0 && sb< n)
            {
                swap(a[sa] , a[sb]);
                sa = sb = -1;
                l++;
                r--;
            }    

        }

        return a;

    }
};
