class Solution {
public:
    vector<int> arrayRankTransform(vector<int>& arr) {
        set<int>s(arr.begin(),arr.end()); //set to remove duplicate values
        unordered_map<int,int>m; // map to place rank and value
        vector<int>out;//output vector

        for(auto val:s)
        {
            //since we are giving sorted values thus... map size+1 would be the rank of next input
            m[val]=m.size()+1;
        }
        for(auto val:arr)
        {
            //now we took the value for the given number from map and put it into output vector
            out.push_back(m[val]);
        }
        return out;

    }
};
```

