        vector<int> sorted_arr(set.begin(), set.end());
        sort(sorted_arr.begin(), sorted_arr.end());

        for(i=0 ; i<arr.size() ; i++)
        {
            l=0, r=sorted_arr.size()-1;
            while(l<=r)
            {
                mid = (l+r)/2;
                if(sorted_arr[mid]==arr[i])
                {
                    rank.push_back(mid+1);
                    break;
                }
                else if(sorted_arr[mid]<arr[i])
                {
                    l = mid+1;
                }
                else
                {
                    r = mid-1;
                }
            }
        }
        return rank;
    }
};
