class Solution {
    public int[] arrayRankTransform(int[] arr) {

        int len = arr.length;
        if(len == 0)    return new int[0];

        int[] sortedArr = arr.clone();

        Arrays.sort(sortedArr);
        HashMap<Integer, Integer> map = new HashMap<>();

        int[] rankList = new int[len];
