class Solution {
public:
    vector<int> arrayRankTransform(vector<int>& arr) {

        priority_queue<pair<int,int>> pq;

        for(int i=0;i<arr.size();i++)
        {
            pq.push({-1*arr[i],i});
        }


        vector<int> ans(arr.size(),0);
        int i=1;
        while(!pq.empty())
        {
            auto temp=pq.top();
            pq.pop();

            ans[temp.second]=i;
            if(!pq.empty()){
                int ele=pq.top().first;
            if(temp.first!=ele)
            {
                i++;
            }
            }
        }

        return ans;
    }
};
