class Solution {
public:
    std::vector<int> decode(std::vector<int>& encoded) {
        const int n = encoded.size() + 1;

        int x = 0;
        for ( int i = 1; i <= n; ++i )
            x ^= i;

        int e = 0;
        for ( int i = 1; i < encoded.size(); i += 2 )
            e ^= encoded[i];

        int po = x ^ e;

        std::vector<int> perm(n, 0);

        perm[0] = po;

        for ( int i = 1; i < n; ++i ) {
            perm[i] = perm[i-1] ^ encoded[i-1];
        }

        return perm;
    }
};
```

