            n = 0
            for i in range(1,len(encoded),2):
                n ^= encoded[i]
            for i in range(1,len(encoded)+2):
                n ^= i
            return n

        n = getFirstDecodedElement(encoded)
        out = [n]
        for i in range(len(encoded)):
            n ^= encoded[i]
            out.append(n)
        return out
