class Solution:
    def decode(self, encoded: List[int]) -> List[int]:
        n = len(encoded) + 1 
        total = 0
        for i in range(1, n + 1):
            total ^= i

        xor_except_the_first = 0
        for i in range(1, len(encoded), 2):
            xor_except_the_first ^= encoded[i]

        res = [0] * n
        res[0] = total ^ xor_except_the_first
        for i in range(len(encoded)):
            res[i + 1] = res[i] ^ encoded[i]
        return res

