class Solution {
public:
    vector<int> decode(vector<int>& E) {
        vector<int> P;
        int A=0,B=0;
        for(int i=1;i<=E.size()+1;i++){
            A^=i;
        }
        for(int i=1;i<E.size();i+=2){
            B^=E[i];
        }
        P.push_back(A^B);
        for(int i=0;i<E.size();i++){
            P.push_back(P[i]^E[i]);
        }
        return P;
    }
};
