class Solution:
    def decode(self, encoded: List[int]) -> List[int]:
        perm1=[i for i in range(1,len(encoded)+2)]
        totalxor=0
        for i in range(len(perm1)):
            totalxor^=perm1[i]

        present=0
        next1=0

        i=0
        rx=0
        while(i<len(encoded)-1):
            if(i==0):
                present=encoded[i]
                next1=present^encoded[i+1]
                rx=present^next1
            else:
                next1=next1^encoded[i+1]
                #print('next1',next1)
                rx^=next1

            i+=1

        for i in range(1,len(encoded)+2):
            if(rx^i==totalxor):
                y=i
                break


        perm=[y]
        for i in range(1,len(perm1)):
            if(i==1):
                perm.append(y^encoded[i-1])
            else:
                y=perm[-1]
                perm.append(y^encoded[i-1])

        return perm
