func decode(encoded []int) []int {
    n := len(encoded) + 1
    sum := 0
    for i := 1; i <= n; i++ {
        sum ^= i
    }

    start := sum
    for i := 1; i < len(encoded); i+= 2 {
        start ^= encoded[i]
    }

    res := []int{start}
    for _, v := range encoded {
        res = append(res, res[len(res) - 1] ^ v)
    }
    return res
