impl Solution {
    pub fn decode(encoded: Vec<i32>) -> Vec<i32> {
        let n = encoded.len();

        let mut t = encoded
            .iter()
            .skip(1)
            .step_by(2)
            .fold((n % 4 == 0) as i32, |acc, x| acc ^ x);

        std::iter::once(t)
            .chain(encoded.iter().map(|x| {
                t ^= x;
                t
            }))
            .collect()
    }
}
