class CustomStack {
    private int index;
    private Deque<Integer> stack = new ArrayDeque<>();
    private int increment[]; // check the range of increment
    public CustomStack(int maxSize) {
        index = -1;
        increment = new int[maxSize];
    }

    public void push(int x) {
        if(stack.size() == increment.length)
            return;
        stack.push(x);
        index++;
    }

    public int pop() {
        if(index == -1)
            return -1;
        int ret = stack.pop();
        int incre = increment[index]; //get increment with index
        if(index > 0){
            increment[index - 1] += increment[index]; // go to previous one
        }
        increment[index] = 0; // set the current one to zero
        index--;
        return ret + incre;
    }

    public void increment(int k, int val) {
        if(index == -1)
            return;
        if(stack.size() < k){
            increment[index] += val; // increment
        }
        else{
            increment[k - 1] += val; // increment
        }
    }
}
