        maxSizeStack[top] = x;        
    }

    public int Pop() 
    {
        if(top == -1)
        {
            return -1;
        }                

        int element = maxSizeStack[top];
        top--;
        return element;        
    }

    public void Increment(int k, int val) 
    {
        for(int i = 0; i < k && i <= top; i++)
        {
            maxSizeStack[i] += val;
        }
    }
}
```

