class CustomStack {
    int size;
    Stack<Integer> st;
    int[] arr;
    boolean[] state;
    int curr;
    int idx;
    public CustomStack(int maxSize) {
        size = maxSize;
        st = new Stack<>();
        arr = new int[maxSize];
        state = new boolean[maxSize];
        curr = 0;
        idx = 0;
    }
    public void push(int x) {
        if(curr < size){
            st.push(x);
            arr[idx++] = x;
            curr++;
        }
    }

    public int pop() {
        if(st.isEmpty()) return -1;
        idx--;
        if(state[idx]){
            int res = arr[idx];
            arr[idx]=0;
            state[idx] = false;
            curr--;
            st.pop();
            return res;
        }else{
            curr--;
            arr[idx]=0;
            state[idx]=false;
            return st.pop();
        }
    }

    public void increment(int k, int val) {
        for(int i=0;i<k && i<arr.length;i++){
            arr[i]+=val;
            state[i] = true;
        }
    }
}
