    int pop() {
        if(v.size()>0) {
            int d=v.back();
            v.erase(v.begin()+v.size()-1);
            return d;
        }
        else {
            return -1;
        }
    }

    void increment(int k, int val) {
        for(int i=0;i<min(int(v.size()),k);i++) {
            v[i]+=val;
        }
    }
};
