class CustomStack {

    private int[] arr;
    private int   i = -1;

    public CustomStack(int maxSize) {
        this.arr = new int[maxSize];
    }

    public void push(int x) {
        if (i+1 < arr.length) {
            arr[++i] = x;
        }
    }

    public int pop() {
        return i < 0 ? i : arr[i--];
    }

    public void increment(int k, int val) {
        for (int j = 0; j < Math.min(k, i+1); j++) {
            arr[j] += val;
        }
    }
}
