        if(st.size()<n)
        {
            st.push_back(x);
        }
    }

    int pop() {
        if(st.size()>0)
        {
            int popped = st[st.size()-1];
            st.erase(st.begin()+st.size()-1);
            return popped;
        }
        return -1;
    }

    void increment(int k, int val) {
        if(k>st.size())
        {
            for(int i=0 ; i<st.size() ; i++)
            {
                st[i] += val;
            }
        }
        else
        {
            for(int i=0 ; i<k ; i++)
            {
                st[i] += val;
            }
        }
    }
};

