    if (this.elements.length < this.maxSize) {
      this.elements.push(x);
    }
  }

  // Time O(1)
  // Space O(1)   
  pop() {
    if (this.elements.length > 0) {
      return this.elements.pop();
    } else {
      return -1;
    }
  }

  // Time O(k) - where k is the number of elements to increment 
  // Space O(1)
  increment(k, val) {
    if (this.elements.length < k) {
      for (let i = 0; i < this.elements.length; i++) {
        this.elements[i] += val;
      }
    } else {
      for (let i = 0; i < k; i++) {
        this.elements[i] += val;
      }
    }
  }
}


