        if(top==1)
            return -1;
        int offset=inc[top-1];
        inc[top-1]=0;
        inc[top-2]+=offset;

        return st[--top]+offset;

    }

    public void increment(int k, int val) {
        inc[0]+=-val;
        inc[(int)Math.min(k,top-1)]+=val;
    }
