class CustomStack {
    int[] arr; // value
    int[] inc; // increment
    int index = -1;
    public CustomStack(int maxSize) {
        arr = new int[maxSize];
        inc = new int[maxSize];
    }

    // O(1)
    public void push(int x) {
        if (index >= arr.length - 1) {
            return;
        }
        arr[++index] = x;
    }

    // O(1)
    public int pop() {
        if (index < 0) {
            return -1;
        }
        if (index > 0) {
            inc[index - 1] += inc[index];
        }
        int val = arr[index] + inc[index];
        inc[index--] = 0;
        return val;
    }

    // O(1)
    public void increment(int k, int val) {
        if (index < 0) {
            return;
        }

        int i = Math.min(k, index + 1) - 1;
        if (i >= 0) {
            inc[i] += val;
        }
    }
}
