class CustomStack {

    int maxSize;
    Stack<Integer> st;

    public CustomStack(int maxSize) {
        this.st = new Stack<Integer>();
        this.maxSize = maxSize;
    }

    public void push(int x) {
        if(st.size()>=maxSize)return;
        st.push(x);
    }

    public int pop() {
        if(st.isEmpty())return -1;
        return st.pop();
    }

    public void increment(int k, int val) {
        Stack<Integer> util = new Stack<Integer>();
        int pass = st.size()-k;
        while(!st.isEmpty()){
            if(pass>0){
                pass--;
                util.push(st.pop());
            }else{
                int tmp = st.pop() + val;
                util.push(tmp);
            }
        }
        while(!util.isEmpty())st.push(util.pop());
    }
}
