class CustomStack {

    LinkNode head = null;
    int maxSize;
    int currentSize;

    public CustomStack(int maxSize) {
        this.maxSize = maxSize;
    }

    public void push(int x) {
        if(currentSize<maxSize) {
            if(head == null) {
                head = new LinkNode(x);
            } else {
                LinkNode node = new LinkNode(x);
                node.next = head;
                head = node;
            }
            currentSize++;
        }
    }

    public int pop() {
        if(currentSize == 0) return -1;
        int result = head.val;
        head = head.next;
        currentSize--;
        return result;
    }

    public void increment(int k, int val) {
        if(currentSize != 0) {
            LinkNode frontNode = head;
            for(int i=0;i<k && frontNode != null;i++) {
                frontNode = frontNode.next;
            }
            LinkNode backNode = head;
            while(frontNode != null) {
                backNode = backNode.next;
                frontNode = frontNode.next;
            }
            while(backNode!=null) {
                backNode.val += val;
                backNode = backNode.next;
            }
        }
    }
}

