class CustomStack {
public:
    // Space Complexity: O(n)
    deque<int> dq;
    int n;

    CustomStack(int maxSize) {
        // Time Complexity: O(1)
        n = maxSize;
    }

    void push(int x) {
        // Time Complexity: O(1)
        if (dq.size() == n) return;
        dq.push_back(x);
    }

    int pop() {
        // Time Complexity: O(1)
        if (dq.empty()) return -1;
        int x = dq.back();
        dq.pop_back();
        return x;
    }

    void increment(int k, int val) {
        // Time Complexity: O(k)
        for (auto it = dq.begin(); it != (k > dq.size() ? dq.end() : dq.begin() + k); ++it)
            *it += val;
    }
};

