    public void push(int x) {
        // Before pushing first check if it is already filled completely just simply return.
        if(index + 1 == value.length)
        {
            return;
        }
        index++;
        value[index] = x;
        //It shows incrememt regarding this value is equal to zero.
        increment[index] = 0;



