    public int pop() {
        //Before popping check if stack is already empty.
        if(index == -1)
        {
            return index;
        }
        // Here we are storing the popped value.
        int x = value[index];
        // Here we are storing the corresponding increment of popped value.
        int increased = increment[index];
        // And we decrease index value.
        index--;

