class CustomStack {
public:
    stack<int> s;
    vector<int> add;

    CustomStack(int maxSize) {
        add.resize(maxSize, 0);
    }

    void push(int x) {
        if ((int)s.size() == (int)add.size()) return;
        s.push(x);
    }

    int pop() {
        if (s.empty()) return -1;
        int sze = s.size();
        int res = s.top() + add[sze - 1];
        if (sze >= 2) {
            add[sze - 2] += add[sze - 1];
        }
        add[sze - 1] = 0;
        s.pop();
        return res;
    }

    void increment(int k, int val) {
        if (s.empty()) return;
        int sze = s.size();
        add[min(sze, k) - 1] += val;
    }
};
