class CustomStack:
    def __init__(self, maxSize: int):
        self.l = 0
        self.data = [0] * maxSize
        self.maxSize = maxSize
        self.inc = [0] * maxSize

    def push(self, x: int) -> None:
        if self.l >= self.maxSize:
            return

        self.data[self.l] = x
        self.inc[self.l] = 0
        self.l += 1

    def pop(self) -> int:
        if self.l <= 0:
            return -1
        self.l -= 1
        inc = self.inc[self.l]
        if self.l - 1 >= 0:
            self.inc[self.l - 1] += inc
        return self.data[self.l] + inc

    def increment(self, k: int, val: int) -> None:
        self.inc[min(k, self.l)-1] += val
