class CustomStack
{
public:
    int Size;
    vector<int> stack;
    CustomStack(int maxSize)
    {
        Size = maxSize;
    }

    void push(int x)
    {
        if (stack.size() < Size)
        {
            stack.push_back(x);
        }
    }

    int pop()
    {
        if (!stack.empty())
        {
            int x = stack.back();
            stack.pop_back();
            return x;
        }
        return -1;
    }

    void increment(int k, int val)
    {
        int n = stack.size();
        for (int i = 0; i < min(k, n); i++)
        {
            stack[i] += val;
        }
    }
};
