class CustomStack {
    private int[] stack;
    private int capacity;
    private int top = -1;

    public CustomStack(int maxSize) {
           this.capacity = maxSize;
           stack = new int[this.capacity];
    }

    public void push(int x) {
       if(top+1 == this.capacity)
           return;

       stack[++top] = x;

    }

    public int pop() {
        if(top < 0)
            return -1;

        int val;
        val = stack[top];

        //for garbage collection
        stack[top--] = Integer.MIN_VALUE;

        return val;

    }

    public void increment(int k, int val) {
        Stack<Integer> inc = new Stack<>();

        while(top+1 > k && top > -1){
            inc.push(stack[top--]);
        }

        while(top > -1){
            int data = stack[top--];
            inc.push(data+val);
        }

        while(!inc.isEmpty()){
            stack[++top] = inc.pop();
        }
    }
}

