class CustomStack {

    int top = -1;
    int[] arr;

    public CustomStack(int maxSize) {
        arr = new int[maxSize];
    }

    public void push(int x) {
        if(top<arr.length-1)
        arr[++top]=x; 
    }

    public int pop() {
        if(top == -1)
            return -1;
        int value = arr[top];
        arr[top] = Integer.MIN_VALUE;
        top--;
        return value;
    }

    public void increment(int k, int val) {
        int increment = -1;
        if(k-1<top)
          increment=k-1;  
        else
          increment=top;           
        for(int i=0;i<=increment;i++)
            arr[i]+=val;         
    }
}

