class CustomStack {
public:
    vector<pair<int, int>> stk;
    int capacity, top;

    CustomStack(int maxSize) : 
        capacity(maxSize), top(-1), stk(maxSize) {}

    void push(int x) {
        if (top == capacity - 1)
            return;
        top++;
        stk[top].first = x;
        stk[top].second = 0;
    }

    int pop() {
        if (top == -1)
            return -1;
        if (top != 0)
            stk[top - 1].second += stk[top].second;
        int val = stk[top].first + stk[top].second;
        top--;
        return val;
    }

    void increment(int k, int val) {
        if (k <= 0 || top == -1)
            return;
        stk[min(k - 1, top)].second += val;
    }
};
