public int pop() {
    return stack.isEmpty() ? -1 : stack.pop();
}

public void increment(int k, int val) {
    Stack<Integer> holder = new Stack<Integer>();
    int addLimit = k > cusMaxSize ? cusMaxSize : k;

    int ctr=stack.size();
    while(!stack.isEmpty()){
        holder.push(stack.pop() + (ctr <= addLimit ? val : 0));    
        ctr--;
    }

    while(!holder.isEmpty()){
        stack.push(holder.pop());
    }

}

