    {
        if(top<size)
        {
            st[++top]=make_pair(x,0);    

        }
    }

    int pop()
    {
        if(top!=0)
        {
          int x=st[top].first;
          int y=st[top].second;
          x+=y;
          top--;
          st[top].second+=y;
          return x;
        }
        return -1;
    }

    void increment(int k, int val)
