```swift
// in the worst scenarios, we are calling storage.count which is an O(n) operation
// you could argue if that is constant, given that we know `maxSize`
final class CustomStack {

    private let maxSize: Int
    private var _storage: [Int]

    init(_ maxSize: Int) {
        self.maxSize = maxSize
        _storage = []
        _storage.reserveCapacity(maxSize)
    }
    // O(n) where n = maxSize
    func push(_ x: Int) {
        guard _storage.count < maxSize else { return }
        _storage.append(x)
    }
    // O(1)
    func pop() -> Int {
        guard let popped = _storage.popLast() else { return -1 }
        return popped
    }
    // O(n) where n = maxSize 
    func increment(_ k: Int, _ val: Int) {
        let K = min(k, _storage.count)
        for i in 0..<K {
            _storage[i] += val
        }
    }

}
