class CustomStack {
public:

    vector<int>incr;
    vector<int>st;
    int i = -1;
    CustomStack(int N) {
        incr.resize(N);
        fill(incr.begin(),incr.end(),0);
        st.resize(N);
        fill(st.begin(),st.end(),0);
    }

    void push(int x) {
        int y = st.size() -1;
        if (i < y){
            st[++i] = x;
        }
    }

    int pop() {
        if(i<0)return -1;
        int x = st[i] + incr[i];
		//incr works like reverse prefix sum. As pop operations are called, incr[i] keeps attaining its correct value
        if(i>0)
        	incr[i-1] += incr[i];
        incr[i]=0;
        i--;
        return x;
    }

    void increment(int k, int val) {
        k = min(k-1,i);
        if(k>=0)
        	incr[k]+=val;
    }
};
