var CustomStack = function(maxSize) {
    this.maxSize = maxSize;
    this.stack = [];
    this.incrementDP = new Array(maxSize);
    this.incrementDP.fill(0);
};

/** 
 * @param {number} x
 * @return {void}
 */
CustomStack.prototype.push = function(x) {
    if (this.stack.length < this.maxSize) {
      this.stack.push(x);
    }
};

/**
 * @return {number}
 */
CustomStack.prototype.pop = function() {
    if (!this.stack.length) return -1;
    let result = this.stack.pop();
    const length = this.stack.length;
    if (this.incrementDP[length]) {
      result += this.incrementDP[length];
      if (length > 0) {
        this.incrementDP[length - 1] += this.incrementDP[length]
      }
      this.incrementDP[length] = 0;
    }
    return result;
};

/** 
 * @param {number} k 
 * @param {number} val
 * @return {void}
 */
CustomStack.prototype.increment = function(k, val) {
    const t = Math.min(k, this.stack.length);
    this.incrementDP[t - 1] += val;
};

