    private int[] array;
    private int size;

    public CustomStack(int maxSize) {
        this.array = new int[maxSize];
    }

    public void push(int x) {
        if (size < array.length) {
            array[size++] = x;            
        }
    }

    public int pop() {
        return size > 0 ? array[--size] : -1;
    }

    public void increment(int k, int val) {
        for(int i = 0 ; i < Math.min(k, size) ; i++) {
            array[i] += val;
        }
    }
