class CustomStack {

    ListNode head;
    ListNode tail;
    int size;
    int max;

    public CustomStack(int maxSize) {

        head = new ListNode(-1);
        tail = new ListNode(-1);
        head.next = tail;
        tail.prev = head;
        size = 0;
        max = maxSize;
    }

    public void push(int x) {

        if(size == max) return;

        ListNode first = head.next;
        ListNode pushed_in = new ListNode(x);
        head.next = pushed_in;
        pushed_in.next = first;
        first.prev = pushed_in;
        pushed_in.prev = head;
        size++;
    }

    public int pop() {

        if(size == 0)   return -1;
        ListNode first = head.next;
        ListNode second = first.next;
        head.next = second;
        second.prev = head;
        size--;

        return first.val;
    }

    public void increment(int k, int val) {

        ListNode curr = tail.prev;
        int i = 0;
        while(curr != head && i < Math.min(k, size))
        {
            curr.addVal(val);
            curr = curr.prev;
            i++;
        }
    }
}

