class CustomStack {



public:

	// value at top and amount by how much it should be incremented

	vector<pair<int,int>> st;

    int maxSize;

    CustomStack(int maxSize) {
        this->maxSize = maxSize;
    }

    void push(int x) {
        if(st.size()==maxSize){
            return;
        }
        st.push_back({x,0});
    }

    int pop() {
        if(st.size()==0)
            return -1;
        int size = st.size();
        if(size!=1){
            st[size-2].second += st[size-1].second;
        }
        int val =  st[size-1].first+st[size-1].second;
        st.pop_back();
        return val;
    }

    void increment(int k, int val) {
        if(st.size()==0){
            return;
        }
        if(st.size()>=k){
            st[k-1].second+=val;
        }else{
            st[st.size()-1].second+=val;
        }
    }
};

