class CustomStack {
    int size;
    stack<int> st;
public:
    CustomStack(int maxSize) {
        size=maxSize;  //initializing the size of the stack
    }
    void push(int x) {
        if(st.size()!=size)  //push down the element if the size of stack is less than maxsize
        {
            st.push(x);   
        }            
    }

    int pop() {
        if(st.size()==0) return -1;  //if stack size is zero then return -1
        int temp=st.top();  //storing top element of the stack, for returning later
        st.pop();  //delete the top element
        return temp;  //return the top element, which we saved earlier
    }

    void increment(int k, int val) {
        stack <int> temp_stk;  //temporary stack for incrementing the bottom k elements

        while(!st.empty())  //firstly, push all elements of the stack into temporary stack 
        {
            temp_stk.push(st.top());
            st.pop();
        }

        while(k-- && !temp_stk.empty())  //now if k>0 and any value exist in temporary stack then push it's top element by incrementing the value by val.
        {
            int temp = temp_stk.top() + val;
            st.push(temp);
            temp_stk.pop();  // delete the top element of the temporary stack
        }

        if(!temp_stk.empty())  //if there exist any element means k become 0, so we will add all remaining elements as it is.
        {
            while(!temp_stk.empty())
            {
                int temp = temp_stk.top();
                st.push(temp);
                temp_stk.pop();
            }
        }
    }
};
