        def getsplit(front,rear):
            if rear-front<1: return [1,0]
            lcase = [[] for i in range(26)]
            ucase = [[] for i in range(26)]


            for i in range(front,rear+1):
                if ord(s[i])-97>=0:  lcase[ord(s[i])-97].append(i)
                else: ucase[ord(s[i])-65].append(i)        
            part = [front-1,rear+1]


            for k in range(26):
                if len(ucase[k])*len(lcase[k])>0 or len(ucase[k]) + len(lcase[k])==0: continue 
                for i in ucase[k]+lcase[k]:
                    part.append(i)  

            if len(part)==2: return [front,rear]      
            part.sort()      
            output = [1,0]   
            for i in range(len(part)-1):
                temp = getsplit(part[i]+1,part[i+1]-1)
                if temp[1]-temp[0]>output[1]-output[0]:
                    output = temp  
            return output

        l,r = getsplit(0,len(s)-1)    
        return s[l:r+1]
