class Solution {
public:
    bool isNice(string &s) {
        unordered_set<char> st(begin(s), end(s));

        for(char &ch : s) {
            if(!st.count(tolower(ch)) || !st.count(toupper(ch)))
                return false;
        }
        return true;
    }
    string longestNiceSubstring(string s) {
        string result = "";
        int n = s.length();

        for(int i = 0; i<n; i++) {
            for(int j = 0; j<n; j++) {
                string temp = s.substr(i, j-i+1);
                if(isNice(temp) && temp.length() > result.length())
                    result = temp;
            }
        }

        return result;
    }
};
