       int l_freq[26]={0} , u_freq[26]={0};
       for(int x = i;x<=j;x++) {
           if(s[x]>='a' && s[x]<='z') 
               l_freq[s[x]-'a']++;
           else 
               u_freq[s[x]-'A']++;
       }
       for(int i=0;i<26;i++) {
           if((l_freq[i]&&!u_freq[i]) ||(!l_freq[i]&&u_freq[i]))
