        for(int i=0;i<n;i++) {
            for(int j=i+1;j<n;j++) {
                if(niceSubstr(s,i,j,n)) {
                    if(maxLen < j-i+1){
                        maxLen = j-i+1;
                        ans = s.substr(i,j-i+1);
                    }                        
                }
            }
        }
        return ans;
    }
};
