class Solution:
    def longestNiceSubstring(self, s: str) -> str:

        if set(s) == set(s.swapcase()):
            return s

        orphan = set(s) - set(s.swapcase())

        return max(map(self.longestNiceSubstring, re.split("|".join(orphan), s)), key=len)

