class Solution:
    def longestNiceSubstring(self, s: str) -> str:
        S = set(s)
        if all(c.upper() in S and c.lower() in S for c in s):
            return s
        for c in S:
            if c.upper() not in S or c.lower() not in S:
                s = s.replace(c,',')
        res = ''
        for s2 in s.split(','):
            tmp = self.longestNiceSubstring(s2)
            if len(tmp) > len(res):
                res = tmp
        return res
