	//There are 26 types of characters. So. let's check if substring has 1 type of character .... 26 type of character.
	//Each check using sliding window algorthim O(N)
	//So. the total is 26 * O(N) => O(N)
	public String longestNiceSubstring(String s) {
        String ans = "";
        for(int i = 1; i <= 26; i++) {
            int[] range = possible(s.toCharArray(), i);
            if(range[1] - range[0] >= ans.length()) ans = s.substring(range[0], range[1] + 1);
        }
        return ans;
    }

    private int[] possible(char[] arr, int total) {
        int[] lowers = new int[26], uppers = new int[26], res = new int[]{0, -1};

        for(int left = 0, right = 0, count = 0; right < arr.length; right++) {
            if ('a' <= arr[right] && arr[right] <= 'z') {
                if(lowers[arr[right] - 'a'] + uppers[arr[right] - 'a'] == 0) count++; //new character
                lowers[arr[right] - 'a']++;  
            } else {
                if(lowers[arr[right] - 'A'] + uppers[arr[right] - 'A'] == 0) count++; //new character
                uppers[arr[right] - 'A']++;
            }
            //character type is too much, move left side of sliding window
            while (total < count) {
                if ('a' <= arr[left] && arr[left] <= 'z') {
                    lowers[arr[left] - 'a']--;
                    if(lowers[arr[left] - 'a'] + uppers[arr[left] - 'a'] == 0) count--;
                } else {
                    uppers[arr[left] - 'A']--;
                    if(lowers[arr[left] - 'A'] + uppers[arr[left] - 'A'] == 0) count--;
                }
                left++;
            }
            //check if this substring is valid. If so, update result
            int valid = 0;
            for (int i = 0; i < 26; i++) valid += (lowers[i] > 0 && uppers[i] > 0)? 1: 0;
            if (valid == count && right - left > res[1] - res[0]) {
                res[0] = left; res[1] = right;
            }
        }

        return res;

    }
