const isNice = (s) => {
    let lower = new Set();
    let upper = new Set();
    for (const c of s) {
        isLowerCaseLetter(c) ? lower.add(c) : upper.add(c);
    }
    for (const lo of lower) {
        if (!upper.has(lo.toUpperCase())) return false;
    }
    for (const up of upper) {
        if (!lower.has(up.toLowerCase())) return false;
    }
    return true;
};

