    bool isNice(string s)
    {
        map<char,int> mp;
        for(auto &i : s) mp[i]++;

        for(auto &i : s)
        {
            if(i >= 'A' && i <= 'Z')
            {
                if(mp[i+32] == 0) return 0;
            }
            else
            {
                if(mp[i-32] == 0) return 0;
            }
        }

        return 1;
    }
    string longestNiceSubstring(string s) {
        string res="";
        int max_length = 0;

        for(int i=0;i<s.length();i++)
        {
            for(int j=i+1;j<s.length();j++)
            {
                string t = s.substr(i,j-i+1);
                if(isNice(t))
                {
                    if(t.length() > max_length)
                    {
                        max_length = t.length();
                        res = t;
                    }
                }
            }
        }

        return res;
    }
};
